import { useEffect, useState } from "react";

function App() {

  const [ listaTarefas, setListaTarefas ] = useState( [] );
  const [ tarefas, setTarefas ] = useState('');

  function addTarefa()
  {
    if( tarefas !== "") {
      setListaTarefas([...listaTarefas, tarefas]);
    }
  }

  function excluirTarefas( index )
  {
      listaTarefas.filter( item => item.index !== index);
  }

    useEffect(() => {
       setTarefas('');
       }, [listaTarefas] )

  return (
    <>
    <header>
      <h1>React DO</h1>
    </header>

      <div>
        <input type="text" name="tarefa" placeholder="Digite sua tarefa" value={tarefas} onChange={(e) => setTarefas( e.target.value)} />
        <button onClick={addTarefa}>Adicionar</button>
      </div>
      <div>
        <ul>
          {listaTarefas.map(( item, index ) => (
              <li key={index}>{item} <button onClick={excluirTarefas(index)} >Excluir</button></li>
          ))}
        </ul>
      </div>
    </>
  );
}

export default App;
